import react from 'react'
import Header from './Header'
import { useState } from 'react'
import Notifications from './Notifications'

import '../componentstyle.css'
const notificationArry=[
    {id:1,name:'Mark Webber', actionType:'reacted to your post',imgUrl:require('./images/avatar-mark-webber.webp'),actionDetail: 'My first tournament today!',timing:'1m ago'},

{id:2,name:'Angela Gray', actionType:'followed you',imgUrl:require('./images/avatar-angela-gray.webp'),actionDetail:'',timing:'5m ago'},
{id:3,name:'Jacob Thompson', actionType:'has joined your group ',imgUrl:require('./images/avatar-jacob-thompson.webp'), actionDetail:'Chess Club',timing:'1 day ago'},
{id:4,name:'Rizky Hasanuddin', actionType:'sent you a private message',imgUrl:require('./images/avatar-rizky-hasanuddin.webp'),actionDetail:'',
message:"Hello, thanks for setting up the Chess Club. I've been a member for a few weeks now and I'm already having lots of fun and improving my game.",timing:"5 days ago"}
,{id:5,name:'Kimberly Smith', actionType:'commented on your picture',imgUrl:require('./images/avatar-kimberly-smith.webp'), actionDetail:'',timing:'1 week ago',pictureUrl:require('./images/image-chess.webp')},
{id:6,name:'Nathan Peterson', actionType:'reacted to your recent post ',imgUrl:require('./images/avatar-nathan-peterson.webp'),actionDetail:"5 end-game strategies to increase your win rate",timing:'2 weeks ago'},
{id:7,name:'Anna Kim', actionType:'left the group',imgUrl:require('./images/avatar-anna-kim.webp'), actionDetail:"Chess Club", timing:'2 weeks ago'}]


const NotficationMainPage =()=>{
const [isRead, setRead] = useState([true,true,true,true,true,true,true])
const clickHandler = (index)=>{
    const newArr = [...isRead]
    newArr[index] = false
    setRead(newArr)

}

return(<div className='main-container'>
<Header />

{notificationArry.map((notifObj,index)=>{
    if(notifObj.message){
         return <Notifications key={notifObj.id} readUnread={isRead[index]} name={notifObj.name}imgSource={notifObj.imgUrl} actionDetail={notifObj.actionDetail} actionType={notifObj.actionType} timing={notifObj.timing} message={notifObj.message}/> 
    }else if(notifObj.pictureUrl){
        return <Notifications key={notifObj.id}  pictureUrl={notifObj.pictureUrl} readUnread={isRead[index]}  name={notifObj.name} imgSource={notifObj.imgUrl} actionDetail={notifObj.actionDetail}actionType={notifObj.actionType} timing={notifObj.timing}/>
    }else{
    return <Notifications key={notifObj.id} readUnread={isRead[index]}  name={notifObj.name} imgSource={notifObj.imgUrl} actionDetail={notifObj.actionDetail}actionType={notifObj.actionType} timing={notifObj.timing}/>

}
})}




</div>

)
}

export default NotficationMainPage


