import react from 'react'
import Image from './Image'
import '../componentstyle.css'

const Notifications = ({name,imgSource,actionType, actionDetail,timing, message,isRead })=>{
console.log(name)
    return(
<div>
<Image className='profile-img' source={imgSource}/>
<p><strong>{name}</strong> {actionDetail}</p>
{isRead && <div style={{backgroundColor:'hsl(1, 90%, 64%)',width:'5px',borderRadius:'50%'}}></div>}
<time>{timing}</time>
{actionType==='privateMessage' && <p>{message}</p>}

</div>


    )
}

export default Notifications
