import react from 'react'
import Image from './Image'
import '../componentstyle.css'

const Notifications = ({name,imgSource,actionType, actionDetail,timing, message,isRead })=>{

    return(
<div>
<Image className='profile-img' source={imgSource}/>
<p className='notific-para'><strong>{name}</strong> {actionDetail}</p>
{/* <div style={{backgroundColor:'hsl(1, 90%, 64%)',width:'50px',borderRadius:'50%',display:'block'}}></div> */}
<svg width="6" height="6" viewBox="0 0 6 6" fill="none" xmlns="http://www.w3.org/2000/svg">
<circle cx="3" cy="3" r="2.5" fill="#FB4E4B" stroke="black"/>
</svg>
<time>{timing}</time>
{actionType==='privateMessage' && <p>{message}</p>}

</div>


    )
}

export default Notifications
