import react from 'react'
import Header from './Header'
import { useState } from 'react'
import Notifications from './Notifications'

import '../componentstyle.css'
const notificationArry=[
    {id:1,name:'Mark Webber', actionType:'reaction',imgUrl:require('./images/avatar-mark-webber.webp'),actionDetail: 'My first tournament today!',timing:'1m ago'},

{id:2,name:'Angela Gray', actionType:'follow',imgUrl:require('./images/avatar-angela-gray.webp'),actionDetail:'followed you',timing:'5m ago'},
{id:3,name:'Jacob Thompson', actionType:'groupJoining',imgUrl:require('./images/avatar-jacob-thompson.webp'), actionDetail:'has joined your group Chess Club',timing:'1 day ago'},
{id:4,name:'Rizky Hasanuddin', actionType:'privateMessage',imgUrl:require('./images/avatar-rizky-hasanuddin.webp'),actionDetail:'sent you a private message',
message:"Hello, thanks for setting up the Chess Club. I've been a member for a few weeks now and I'm already having lots of fun and improving my game.",timing:"5 days ago"}
,{id:5,name:'Kimberly Smith', actionType:'comment',imgUrl:require('./images/avatar-kimberly-smith.webp'), actionDetail:'commented on your picture',timing:'1 week ago'},
{id:6,name:'Nathan Peterson', actionType:'reaction',imgUrl:require('./images/avatar-nathan-peterson.webp'),actionDetail:"reacted to your recent post 5 end-game strategies to increase your win rate",timing:'2 weeks ago'},
{id:7,name:'Anna Kim', actionType:'groupLeft',imgUrl:require('./images/avatar-anna-kim.webp'), actionDetail:"left the group Chess Club", timing:'2 weeks ago'}]


const NotficationMainPage =()=>{
const [isRead, setRead] = useState([true,true,true,true,true,true,true])
const clickHandler = ()=>{
    setRead()
}

return(<div className='main-container'>
<Header />

{notificationArry.map((notifObj,index)=>{
    if(!notifObj.message){
   return <Notifications key={notifObj.id} readUnread={isRead[index]}  name={notifObj.name} imgSource={notifObj.imgUrl} actionDetail={notifObj.actionDetail}actionType={notifObj.actionType} timing={notifObj.timing}/>
}else{
    return <Notifications key={notifObj.id} readUnread={isRead[index]} name={notifObj.name}imgSource={notifObj.imgUrl} actionDetail={notifObj.actionDetail} actionType={notifObj.actionType} timing={notifObj.timing} message={notifObj.message}/>
}
})}




</div>

)
}

export default NotficationMainPage


